import React, { useState, useEffect } from "react";
import "./style.css"
function Index() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch(
      "https://api.themoviedb.org/3/movie/popular?api_key=ebab107d0e771faee711646843039664&language=en-US"
    )
      .then((response) => response.json())
      .then((data) => {
        // Sort movies by vote_average in descending order
        const sortedMovies = data.results.sort(
          (a, b) => b.vote_average - a.vote_average
        );
        setMovies(sortedMovies);
      })
      .catch((error) => console.error("Error fetching data: ", error));
  }, []);

  return (
    <>
      <div className="navBar">
        <div className="logo">
          {/* Your logo component */}
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/69/IMDB_Logo_2016.svg/2560px-IMDB_Logo_2016.svg.png"
            alt="logo-imdb"
            className="navBarIcon"
          />
        </div>
        <div className="all">
          {/* Link to home */}
          <a href="/">Top Rated</a>
        </div>
      </div>
      <div className="movieList">
        <h2>Top Rated Movies</h2>
        {movies.map((movie) => (
          <div key={movie.id} className="movieCard">
            <h3 className="movieTitle">{movie.title}</h3>
            <p className="movieRating">Rating: {movie.vote_average}</p>
            <p className="movieOverview">{movie.overview}</p>
          </div>
        ))}
      </div>
    </>
  );
}

export default Index;
